import { createAction } from '@reduxjs/toolkit';
import { nanoid } from 'nanoid';

function addTodoPrepare({ name, description, deadline, priority }) {
    return {
        payload: {
            name: name,
            deadline: deadline,
            description: description,
            id: nanoid(),
            completed: false,
            priority: priority,
        },
    };
}

const addTodo = createAction('todos/addTodo', addTodoPrepare);
const deleteTodo = createAction('todos/deleteTodo');
const toggleTodo = createAction('todos/toggleTodo');
const filterTodo = createAction('todos/filterTodo');

console.log(addTodo());

export { addTodo, deleteTodo, toggleTodo, filterTodo };
