import React, { useContext, useState, useEffect } from 'react';

import { useSelector, useDispatch } from 'react-redux';

import {
  useGetTodosQuery,
  useAddTodoMutation,
  useUpdateTodoMutation,
  useDeleteTodoMutation,
} from 'service/apiService';

import { TodoListWrapper } from './components/TodoListWrapper';
import TodoFilter from './components/TodoFilter';
import TodoEditor from './components/TodoEditor';
import ModalWindow from 'components/ModalWindow';

import {
  StyledList,
  HeaderTextContainer,
  Text,
} from './styles/TodoList.styled';

import { themeContext } from 'context/themeContext';

import { ButtonStyled } from 'components/CommonComponents';
import CreateTwoToneIcon from '@mui/icons-material/CreateTwoTone';

import TodoContainer from './components/TodoContainer';

import { useModal } from 'hooks';

function Todos() {
  /*
   * useDispatch - connect actions with active UI elements
   * useSelector - returns state elements
   */

  const [showModal, toggleModal] = useModal(false);

  const {
    data: todos,
    isLoading,
    isSuccess,
    isError,
    error,
  } = useGetTodosQuery();

  return (
    <>
      <TodoContainer
        // listRef={listRef}
        // diffLength={diff.length}
        // completedTasksNumber={completedTasksNumber}
        todos={todos}
        // filterTasks={filterTodos}
        toggleModal={toggleModal}
        // deleteTask={deleteTask}
        // toggleCompleted={toggleCompleted}
        // addTask={addTask}
        //   showModal={showModal}
      />
    </>
  );
}

export default Todos;
