import { VideoListWrapper } from './Player.styled';
import PropTypes from 'prop-types';
import Button from '@mui/material/Button';


export function VideoList({
  videos,
  onSelect,
  colors
}) {
    console.log(onSelect);
  return (
    <VideoListWrapper>
      {videos.map(({ id, link }, idx) => (
        <li key={id}>
          <Button style={{height: '40px', padding: '15px', fontSize: '12px', marginBottom: '10px', marginRight: '20px'}} colors={colors} type="button" variant="contained" onClick={() => onSelect(link)} >Video: {idx}</Button>
        </li>
      ))}
    </VideoListWrapper>
  );
};


VideoList.propTypes = {
  videos: PropTypes.arrayOf(PropTypes.shape({
    id: PropTypes.number,
    link: PropTypes.string
  })).isRequired,
  onSelect: PropTypes.func.isRequired,
  colors: PropTypes.objectOf(PropTypes.string).isRequired,
};
