import { Outlet } from 'react-router-dom';
import PropTypes from 'prop-types';
import { RoutesStyled, List } from './ThemeSwitcher.styled';
import { Link } from 'react-router-dom';
import Button from '@mui/material/Button';

import {themeContext} from '../context/authContext';

const btnStyles = ({btnTextColor, btnBgColor, btnBorderColor}) => {
  return (
    {
    color: `${btnTextColor}`, 
    backgroundColor: `${btnBgColor}`,
    border: `1px solid ${btnBorderColor}`,
    }
  );
}


export const Layout = ({ children }) => {

  return (
  <themeContext.Consumer>
  {({mainTheme}) => {
    const {colors} = mainTheme;
    console.log(btnStyles(colors))

    return (
    <div>
      <RoutesStyled>
        <List>
          <Link style={{textDecoration: 'none'}} to='/react-homework-template/Counter'>
            <Button style={{
                color: `${colors.btnTextColor}`, 
                backgroundColor: `${colors.btnBgColor}`
              }} 
              variant="contained" 
              type="button">
              Counter 
            </Button>
            </Link>
        </List>
        <List>
          <Link style={{textDecoration: 'none'}} to='/react-homework-template/SignupForm'>
            <Button style={ btnStyles(colors)
                // color: `${colors.btnTextColor}`, 
                // backgroundColor: `${colors.btnBgColor}`
              }
              variant="contained" 
              type="button">
              SignupForm
            </Button>
            </Link>
        </List>
        <List>
          <Link style={{textDecoration: 'none'}} to='/react-homework-template/ColorPicker'>
            <Button style={{
                color: `${colors.btnTextColor}`, 
                backgroundColor: `${colors.btnBgColor}`
              }} 
              variant="contained" 
              type="button">
              ColorPicker
            </Button>
            </Link>
        </List>
        <List>
          <Link style={{textDecoration: 'none'}} to='/react-homework-template/Clock'>
            <Button style={{
                color: `${colors.btnTextColor}`, 
                backgroundColor: `${colors.btnBgColor}`
              }} 
              variant="contained" 
              type="button">
              Clock
            </Button>
            </Link>
        </List>
        <List>
          <Link style={{textDecoration: 'none'}} to='/react-homework-template/News'>
            <Button style={{
                color: `${colors.btnTextColor}`, 
                backgroundColor: `${colors.btnBgColor}`
              }} 
              variant="contained" 
              type="button">
              News
            </Button>
            </Link>
        </List>
        <List>
          <Link style={{textDecoration: 'none'}} to='/react-homework-template/Video'>
            <Button style={{
                color: `${colors.btnTextColor}`, 
                backgroundColor: `${colors.btnBgColor}`
              }} 
              variant="contained" 
              type="button">
              Video
            </Button>
            </Link>
        </List>    
        <List>
          <Link style={{textDecoration: 'none'}} to='/react-homework-template/Reader'>
            <Button style={{
                color: `${colors.btnTextColor}`, 
                backgroundColor: `${colors.btnBgColor}`
              }} 
              variant="contained" 
              type="button">
              Reader
            </Button>
            </Link>
        </List>     
        {children} 
      </RoutesStyled>
      <Outlet />
    </div>
      )}}
    </themeContext.Consumer>    
  );  
};

Layout.propTypes = {
  children: PropTypes.node.isRequired,
}
