import React, { Component, useState, useEffect } from 'react';
import PropTypes from 'prop-types';

import { Controls } from '../components/Reader/Controls';
import { Progress } from '../components/Reader/Progress';
import { TextWrapper } from '../components/Reader/TextWrapper';

import { themeContext } from "context/authContext";

const LS_KEY = 'reader_item_index';
const LS_KEY_Hooks = 'reader_Hooks';

class ReaderClass extends Component {
  state = {
    index: localStorage.getItem(LS_KEY) ? Number(localStorage.getItem(LS_KEY)) : 0,
  };

  changeIndex = value => {
    const publicationNumber = this.props.publications.length;
    const { index } = this.state;

    // use switch to check if the index is last or first
    switch (index + value) {
      case publicationNumber:
      this.setState({ index: 0 });
      break;
      case -1:
      this.setState({ index: publicationNumber - 1 });
      break;
      default:
      this.setState(state => ({ index: state.index + value }));
    };
  };

  componentDidUpdate(_, prevState) {
      if (prevState.index !== this.state.index) {
        localStorage.setItem(LS_KEY, Number(this.state.index)); // -> save index to localStorage
      };
  };

  // componentDidMount() {
  //     const savedState = localStorage.getItem(LS_KEY); // -> take index from localStorage
  //     if (savedState) {
  //       this.setState({ index: Number(savedState) });
  //     };
  // };

  render() {
    const { colors, publications } = this.props;
    const { index } = this.state;
    const currentPublication = publications[index];

    /*
    * It is good practice to split DOM tree to small elements, which can good managed
    */
    return (
      <>
        <div style={{position: 'absolute', left: '50%', transform: 'translate(-50%,0)'}}>
          <Controls
            onClick={this.changeIndex}
            colors={colors}
          // index={index}
          // publicationsLength={publications.length}
          >
            <Progress
              colors={colors}
              current={index}
              total={publications.length}
            />
          </Controls>
        </div>
        <TextWrapper
          publication={currentPublication}
          colors={colors}
        />
      </>
    );
  };
};



const ReaderHooks = ({publications}) => {
  const [index, setIndex] = useState(() => {
    return localStorage.getItem(LS_KEY_Hooks) ? Number(localStorage.getItem(LS_KEY_Hooks)) : 0
  });

  const changeIndex = value => {
    const publicationNumber = publications.length;
    switch (index + value) {
      case publicationNumber:
        setIndex(0)
      break;
      case -1:
        setIndex(publicationNumber - 1)
      break;
      default:
        setIndex(prevState => prevState + value)
    };
  };

  // useEffect(() => {
  //   const savedState = localStorage.getItem(LS_KEY);
  //   if (savedState) {
  //     setIndex(Number(savedState));
  //   };
  // },[])

  useEffect(() => {
    localStorage.setItem(LS_KEY_Hooks, Number(index)); 
  },[index])

  const currentPublication = publications[index];

  return (
    <themeContext.Consumer>
    {({mainTheme}) => (
    <>
      <div style={{position: 'absolute', left: '50%', transform: 'translate(-50%,0)'}}>
        <Controls
          onClick={changeIndex}
          colors={mainTheme.colors}
          // index={index}
          // publicationsLength={publications.length}
          >
          <Progress
            colors={mainTheme.colors}
            current={index}
            total={publications.length}
          />
        </Controls>
      </div>
      <TextWrapper
        publication={currentPublication}
        colors={mainTheme.colors}
      />
    </>
    )}
    </themeContext.Consumer>    
  );
};


export const Reader = ({publications, colors}) => {
  return (
    <div>
      <ReaderClass publications={publications} colors={colors}/>
      <ReaderHooks publications={publications}/>
    </div>
  )
}

Reader.propTypes = {
  publications: PropTypes.arrayOf(PropTypes.shape({
    text: PropTypes.string.isRequired,
    title: PropTypes.string.isRequired
  })).isRequired,
  colors: PropTypes.objectOf(PropTypes.string).isRequired,
};
