import React, { Component } from 'react';
import { createGlobalStyle } from 'styled-components'
import { BrowserRouter, Routes, Route } from 'react-router-dom';

import NightlightTwoToneIcon from '@mui/icons-material/NightlightTwoTone';
import LightModeTwoToneIcon from '@mui/icons-material/LightModeTwoTone';
import { IconButton } from '@mui/material';

import { ThemeSwitcher } from "./ThemeSwitcher.styled.jsx";

import CounterPage from '../pages/Counter';
import { SignupForm } from '../pages/SignupForm';
import ColorPicker from '../pages/ColorPicker';
import { Clock } from '../pages/Clock';
import { News } from '../pages/News';
import { Video } from '../pages/Video';
import { Reader } from '../pages/Reader';

import videos from '../data/video.json';
import publications from '../data/publications.json';

import Appbar from './Appbar';


import {themeContext} from '../context/authContext';

console.log(themeContext);

// const themes = {
//   light: {
//     colors: {
//       globalBgColor: "white",
//       btnTextColor: "white",
//       btnBgColor: "#1976d2",
//       btnBorderColor: "blue",
//       hoverBtnColor: "white",
//       hoverBtnBgColor: "#1565c0",
//       containerBorderColor: "blue",
//       containerBgColor: "#fff",
//       counterColor: "blue",
//       reverseBtnBorderColor: "blue",
//       placeHolderText: "blue",
//     },
//     id: 'light'
//   },

//   dark: {
//     colors: {
//       globalBgColor: "black",
//       btnTextColor: "#FFF",
//       btnBgColor: "purple",
//       btnBorderColor: "#FFF",
//       hoverBtnColor: "#FFF",
//       hoverBtnBgColor: "#a657f2",
//       containerBorderColor: "purple",
//       containerBgColor: "aqua",
//       counterColor: "#fff",
//       reverseBtnBgColor: "#fff",
//       reverseBtnBorderColor: "black",
//       placeHolderText: "#fff",
//     },
//     id: 'dark'
//   }
// };

const GlobalStyle = createGlobalStyle`
* {
  box-sizing: border-box;
  }
  html {
    scroll-behavior: smooth;
  }
  body {
    background-color: ${props => props.mainTheme.colors.globalBgColor};
    min-height: 100vh;
    margin: 0;
    font-family: 'Roboto', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Oxygen', 'Ubuntu',
    'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif;
    line-height: 1.5;
    scroll-behavior: smooth;
  }
  img {
    display: block;
    max-width: 100%;
  }
  ul {
    padding: 0;
    margin: 0;
    list-style: none;
  }
  h1,
  h2,
  h3,
  h4,
  h5,
  h6,
  p {
    margin: 0;
  }
`;

const colorPickerOptions = [
  { label: 'red', color: 'red' },
  { label: 'green', color: 'green' },
  { label: 'blue', color: 'blue' },
  { label: 'yellow', color: 'yellow' },
  { label: 'black', color: 'black' },
  { label: 'white', color: 'white' },
  { label: 'pink', color: 'pink' },
  { label: 'purple', color: 'purple' },
  { label: 'orange', color: 'orange' },
]

export default class App extends Component {
  // state = {
  //   visible: false,

  //   theme: themes.light,
  //   oppositeTheme: themes.dark,
  // };

  // changeTheme = () => {
  //   if (this.state.theme === themes.dark) {
  //     this.setState({ theme: themes.light, oppositeTheme: themes.dark});
  //   }
  //   if (this.state.theme === themes.light) {
  //     this.setState({ theme: themes.dark, oppositeTheme: themes.light});
  //   }
  // };


  render() {
    return (
      <themeContext.Consumer>
      {({mainTheme, changeTheme}) => {
        console.log(mainTheme.colors) 
        return (
        <div>
          <GlobalStyle theme={mainTheme} />
          <ThemeSwitcher colors={mainTheme.colors}>
            <IconButton onClick={changeTheme} aria-label="change theme">
              {
              mainTheme.id === 'light' ? 
                <NightlightTwoToneIcon fontSize='large' color="primary" /> : 
                <LightModeTwoToneIcon fontSize='large' color="secondary"/>
              }
            </IconButton>
          </ThemeSwitcher>

          <BrowserRouter>
            <Routes>
              <Route path="/react-homework-template/" element={<Appbar colors={mainTheme.colors}/>}>
                <Route path="/react-homework-template/Counter" element={<CounterPage colors={mainTheme.colors} />} />
                <Route path="/react-homework-template/SignupForm" element={<SignupForm colors={mainTheme.colors} />} />
                <Route path='/react-homework-template/ColorPicker' element={<ColorPicker colors={mainTheme.colors} options={colorPickerOptions} />} />
                <Route path="/react-homework-template/Clock" element={<Clock colors={mainTheme.colors} />} />
                <Route path="/react-homework-template/News" element={<News colors={mainTheme.colors} />} />
                <Route path="/react-homework-template/Video" element={<Video colors={mainTheme.colors} videos={videos} />} />
                <Route path="/react-homework-template/Reader" element={<Reader colors={mainTheme.colors} publications={publications} />} />
              </Route>
            </Routes>
          </BrowserRouter>
        </div>
       )}}
      </themeContext.Consumer>
    );
  };
};
