import { createAction, createReducer, current } from '@reduxjs/toolkit';

const initialState = [];
const addTodo = createAction('todos/addTodo');
const deleteTodo = createAction('todos/deleteTodo');
const toggleTodo = createAction('todos/toggleTodo');

export const todoReducer = createReducer(initialState, builder => {
    builder
        .addCase(addTodo, (state, { payload }) => state.todos.push(payload))
        .addCase(deleteTodo, (state, { payload }) =>
            state.todos.filter(({ id }) => id !== payload)
        )
        .addCase(toggleTodo, (state, { payload }) =>
            state.todos.map(todo =>
                todo.id === payload ? {...todo, completed: !todo.completed } : todo
            )
        )
        .addDefaultCase(state => {
            return state;
        });
});

