import { useState } from 'react';
import PropTypes from 'prop-types';

import { themeContext } from "context/authContext";

import { Player } from '../components/Player/Player';
import { VideoList } from '../components/Player/VideoList';
import { VideoContainer, PlayerWrapper } from '../components/Player/Player.styled';

export function Video ({colors, videos}) {
  const [ selectedVideo, setSelectedVideo ] = useState(null);
  return (
    <themeContext.Consumer>
    {({mainTheme}) => (      
    <VideoContainer colors={ colors }>
      <h1>Selected video: { selectedVideo }</h1>
      <PlayerWrapper>
        <VideoList videos={ videos } onSelect={ setSelectedVideo } colors={ colors } />
        <Player url={ selectedVideo }  colors={ colors }/>
      </PlayerWrapper>
    </VideoContainer>
    )}
    </themeContext.Consumer>          
  );
};


/*
* Use Proptypes for checking type of props data of the component
*/


Video.propTypes = {
  videos: PropTypes.arrayOf(PropTypes.shape({
    id: PropTypes.number.isRequired,
    link: PropTypes.string.isRequired
  })).isRequired,
  colors: PropTypes.objectOf(PropTypes.string).isRequired,
};
